mapper.update:

update gift_return_commodity
set gift_return_id = #{gift_return_id,jdbcType=INTEGER},
  return_num = #{return_num,jdbcType=INTEGER},
  gift_commodity_id = #{gift_commodity_id,jdbcType=INTEGER}
where gift_return_commodity_id = #{gift_return_commodity_id,jdbcType=INTEGER}

mapper.select:

select gift_return_commodity_id, gift_return_id, return_num, gift_commodity_id
from gift_return_commodity
where gift_return_commodity_id = #{gift_return_commodity_id,jdbcType=INTEGER}
