mapper.update:

update sales_target
set position_id = #{position_id,jdbcType=INTEGER},
  sales_target_year = #{sales_target_year,jdbcType=INTEGER},
  sales_target_year_amount = #{sales_target_year_amount,jdbcType=DECIMAL}
where sales_target_id = #{sales_target_id,jdbcType=INTEGER}

mapper.select:

select sales_target_id, position_id, sales_target_year, sales_target_year_amount
from sales_target
where sales_target_id = #{sales_target_id,jdbcType=INTEGER}
